import { Formik, Form, Field, ErrorMessage } from "formik"
import * as yup from "yup"
import { useNavigate, Link, useParams } from "react-router-dom"
import axios from "axios"
import React, { useEffect, useState } from "react"



export function CrudEdit({ ProductID }) {
    const [product, setProduct] = useState([{}]);
    const navigate = useNavigate();
    // const params = useParams();

    useEffect(() => {
        axios.get(`http://127.0.0.1:8080/details/${params.id}`)
            .then((response) => {
                setProduct(response.data)
            })
    }, [ProductID])

    function handleChange(e) {
        setProduct(e.target.value);
    }

    const handleSubmit = (e) => {
        e.preventDefault();

        axios.put(`http://127.0.0.1:8080/updateproduct/${params.id}`, product)
            .then((response) => {
                alert("Record Updated Successfully")
                navigate("/products")
            })
    }
    // axios.put(`http://127.0.0.1:8080/updateproduct/${params.id}`, product)
    //     .then((response) => {
    //         alert("Record Updated Successfully");
    //         navigate("/products");
    //     })
    // };



    return (
        <div className="container-fluid">
            <h2>Update Product</h2>
            <Formik
                initialValues={{
                    ProductID: product.ProductID,
                    Name: product.Name,
                    Price: product.Price,
                    Stock: product.Stock
                }}
                validationSchema={
                    yup.object({
                        Name: yup
                            .string()
                            .required("Name is Required"),
                        Price: yup
                            .number()
                            .min(1)
                            .required("Price is Required"),
                        Stock: yup
                            .boolean()
                    })
                }
                onSubmit={handleChange}
            >
                <Form>
                    <dl>

                        <dt>Name</dt>
                        <dd><Field type="text"
                            onChange={handleChange} name="Name"
                            value={product[0].Name} />
                        </dd>

                        <dt>Price</dt>
                        <dd><Field type="number"
                            onChange={handleChange} name="Price"
                            value={product[0].Price} />
                        </dd>

                        <dt>Stock</dt>
                        <dd className="form-switch">
                            <Field className="form-check-input" type="checkbox" name="Stock"
                                checked={product[0].Stock} />Available</dd>
                    </dl>
                    <button type="submit" className="btn btn-primary" >Save</button>
                    <Link className="ms-3" to="/products">View Product</Link>
                </Form>
            </Formik>
        </div>
    )
}
