import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

export function ShopperDetails() {
    const [product, setProduct] = useState({ id: 0, title: '', price: 0, rating:{rate: 0, count: 0 }});
const params = useParams();
useEffect(() => {
    axios({
        method: 'get',
        url: `https://fakestoreapi.com/products/${params.id}`,
        url: `https://fakestoreapi.com/products/${params.title}`,
        url: `https://fakestoreapi.com/products/${params.price}`,
        url: `https://fakestoreapi.com/products/${params.rating}`,
    }).then((response) => {
        setProduct(response.data);
    })
}, [params.id, params.title, params.price, params.rating]);

return (
    <div className="container-fluid">
        <h2>Product Details</h2>
        <div className="row">
            <div className="col-4">
                <img src={product.image} width="200" height="200"/>
            </div>
            <div className="col-8">
                <dl>
                    <dt>Title</dt>
                    <dd>{product.title}</dd>
                    <dt>Price</dt>
                    <dd>{product.price}</dd>
                    <dt>Rating</dt>
                    <dd><span className="bi bi-star-fill text-warning"></span>{product.rating.rate} [{product.rating.count}]</dd>
                </dl>
            </div>
        </div>
    </div>
);
}
