import { Formik, Form, Field, ErrorMessage } from "formik";
import * as yup from "yup";
import { Link, useNavigate } from "react-router-dom";
import axios from "axios";
import { useState, useEffect } from "react";

export function CrudCreate() {
    const navigate = useNavigate();
    const [products, setProducts] = useState([]);
    const [style, setStyle] = useState({});
    const [idError, setIdError] = useState("");

    useEffect(() => {
        axios
            .get("http://127.0.0.1:8080/products")
            .then((response) => {
                setProducts(response.data);
            })
    }, []);

    function VerifyId(e) {
        // alert(e.target.value);
        var id = parseInt(e.target.value);
        for (var product of products) {
            if (product.ProductId === id && id !== 0) {
                setIdError("ProductId Taken - Please Try Another");
                break;
            } else {
                setIdError("");
                setStyle("text-success");
            }
        }
    }
    return (
        <div>
            <h2>Add New Product</h2>
            <Formik
                initialValues={{
                    ProductId: 0,
                    Name: "",
                    Price: 0,
                    Stock: true,
                }}
                onSubmit={
                    (values) => {
                        axios({
                            method: "post",
                            url: "http://127.0.0.1:8080/addproducts",
                            data: values
                        }).then(() => {
                            alert("Product Added");
                            navigate("/products");
                        });
                    }}
            >
                {
                    <Form>
                        <dl>
                            <dt>ProductId</dt>
                            <dd>
                                <Field type="number" name="ProductId" onKeyUp={VerifyId} placeholder="ProductId" />
                            </dd>
                            <dd className="text-danger">{idError}</dd>

                            <dt>Name</dt>
                            <dd>
                                <Field type="text" name="Name" placeholder="Name" />
                            </dd>

                            <dt>Price</dt>
                            <dd>
                                <Field type="number" name="Price" placeholder="Price" />
                            </dd>

                            <dt>Stock</dt>
                            <dd className="form-switch">
                                <Field className="form-check-input" type="checkbox" name="Stock" />Available
                            </dd>
                            <button type="submit" className="btn btn-primary" >Add Product</button>
                            <Link to="/products" className="ms-2">View Products</Link>
                        </dl>
                    </Form>
                }
            </Formik>
        </div>
    );
}
