import { Formik, Form, Field, ErrorMessage } from "formik"
import * as yup from "yup"
import { useNavigate, Link, useParams } from "react-router-dom"
import axios from "axios"
import React, { useEffect, useState } from "react"



export function CrudEdit() {
    const navigate = useNavigate();
    const params = useParams();
    const [product, setProduct] = useState([{}]);

    useEffect(() => {
        axios.get(`http://127.0.0.1:8080/details/${params.id}`)
            .then((response) => {
                setProduct(response.data)
                // alert("Record Updated Successfully")
            // navigate("/products")
            })
    }, [params.id])


    // if (!product) {
    //     return <div>Loading...</div>;
    //   }

    function handleChange(e) {
        setProduct(e.target.value);
    }

    // const handleSubmit = (e) => {
    //     e.preventDefault();

    //     axios.put(`http://127.0.0.1:8080/updateproduct/${params.id}`, product)
    //         .then((response) => {
    //             alert("Record Updated Successfully")
    //             navigate("/products")
    //         })
    // }
    // axios.put(`http://127.0.0.1:8080/updateproduct/${params.id}`, product)
    //     .then((response) => {
    //         alert("Record Updated Successfully");
    //         navigate("/products");
    //     })
    // };



    return (
        <div className="container-fluid">
            <h2>Update Product</h2>
            <Formik
                initialValues={{
                    // ProductID: product.ProductID,
                    Name: product.Name,
                    Price: product.Price,
                    Stock: product.Stock
                }}
                validationSchema={
                    yup.object({
                        Name: yup
                            .string()
                            .required("Name is Required"),
                        Price: yup
                            .number()
                            .min(1)
                            .required("Price is Required"),
                        Stock: yup
                            .boolean()
                    })
                }
                // onSubmit={handleChange}
                onSubmit={(values) => {
                    axios.put(`http://127.0.0.1:8080/updateproduct/${params.id}`, values)
                        .then((response) => {
                            alert("Record Updated Successfully");
                            navigate("/products");
                        })
                }}
            >
                <Form>
                    <dl>

                        <dt>Name</dt>
                        <dd><Field type="text" name="Name"
                           onchange={handleChange} value={product[0].Name} />
                        </dd>

                        <dt>Price</dt>
                        <dd><Field type="number" name="Price"
                           onchange={handleChange} value={product[0].Price} />
                        </dd>

                        <dt>Stock</dt>
                        <dd className="form-switch">
                            <Field className="form-check-input" type="checkbox" name="Stock"
                                checked={product.Stock} />Available</dd>
                    </dl>
                    <button type="submit" className="btn btn-primary" >Save</button>
                    <Link className="ms-3" to="/products">View Product</Link>
                </Form>
            </Formik>
        </div>
    )
}
