import { Link, useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";

const Cart = () => {

    const [cartItems, setCartItems] = useState([]);
    const [cartItemsCount, setCartItemsCount] = useState(0);

    const params = useParams();

    function addToCart(e) {
        // alert(e.currentTarget.name);
        axios
          .get(`http://fakestoreapi.com/products/${e.currentTarget.name}`)
          .then((response) => {
            alert(`Added ${response.data.title} to cart`);
            cartItems.push(response.data);
            setCartItemsCount(cartItems.length);
          });
        console.log(cartItems);
        console.log(cartItemsCount);
      }
      function removeCartItem(e) {
        alert(`Remove from cart`);
        cartItems.splice(e.currentTarget.id, 1);
        setCartItemsCount(cartItems.length);
      }

    useEffect(() => {
        axios.get(`https://fakestoreapi.com/products/${params.id}`)
            .then((response) => {
                setCartItems(response.data);
            })
    }, []);

    return (
        <div>
            <h1>{cartItems.title}</h1>
            <img alt={cartItems.title} src={cartItems.image} width="200" height="200" />
            <p>{cartItems.price}</p>
            <p>{cartItems.description}</p>
            <p>{cartItems.category}</p>

            <Link to="/"><button className="btn btn-primary">Back</button></Link>
            <button onClick={addToCart} className="btn btn-warning" name={cartItems.id}>Add to Cart</button>
        
        </div>
    )
}

export default Cart
