import { Formik, Form, Field, ErrorMessage } from "formik";
import * as yup from "yup";
import { useNavigate, Link, useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";

export function CrudEdit() {
    const navigate = useNavigate();
    const params = useParams();
    const [product, setProduct] = useState({ ProductID: 0, Name: "", Price: 0, Stock: true });

    useEffect(() => {
        axios.get(`http://127.0.0.1:8080/details/${params.id}`)
            .then((response) => {
                setProduct(response.data);
            })
            .catch((error) => {
                console.error("Error fetching product details:", error);
            });
    }, [params.id]);

    const initialValues = {
        Name: product.Name,
        Price: product.Price,
        Stock: product.Stock
    };

    const validationSchema = yup.object().shape({
        Name: yup.string().required("Name is required"),
        Price: yup.number().min(0, "Price must be a positive number").required("Price is required"),
        Stock: yup.boolean().required("Stock status is required")
    });

    const handleSubmit = (values) => {
        axios.put(`http://127.0.0.1:8080/updateproduct/${params.id}`, values)
            .then((response) => {
                console.log("Product updated successfully:", response.data);
                navigate("/products");
            })
    };

    return (
        <div className="container-fluid">
            <h2>Edit Product</h2>
            <Formik
                initialValues={initialValues}
                validationSchema={validationSchema}
            >
                onSubmit={handleSubmit}
                <Form>
                    <div>
                        <label htmlFor="Name">Name:</label>
                        <Field type="text" id="Name" name="Name" />
                        <ErrorMessage name="Name" component="div" className="error" />
                    </div>
                    <div>
                        <label htmlFor="Price">Price:</label>
                        <Field type="number" id="Price" name="Price" />
                        <ErrorMessage name="Price" component="div" className="error" />
                    </div>
                    <div>
                        <label htmlFor="Stock">Stock:</label>
                        <Field type="checkbox" id="Stock" name="Stock" />
                        <ErrorMessage name="Stock" component="div" className="error" />
                    </div>
                    <button type="submit">Save</button>
                    <Link to="/products">Cancel</Link>
                </Form>
            </Formik>
        </div>
    );
}
