import axios from "axios";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export function CrudEdit() {
    const params = useParams();
    const [products, setProducts] = useState([{}]);
    // const [products, setProducts] = useState([{productId:0, name:"", price:0, stock:false}]);

    
    useEffect(() => {
        axios({
            method: 'get',
            url: `http://localhost:8080/details/${params.id}`
        }).then((response) => {
            setProducts(response.data);
        })
    }, []);
    return (
        <div className="container-fluid">
            <h2>Edit Product</h2>
            <Formik
                initialValues={
                    {
                        ProductID: 0,
                        Name: '',
                        Price: 0,
                        Stock: false
                    }
                }
                onSubmit={
                    (values) => {
                        axios({
                            method: 'put',
                            url: `http://localhost:8080/updateproduct/${values.ProductID}`,
                            data: values
                        })
                            .then((response) => {
                                setProducts(products.Name);
                                setProducts(products.Price);
                                setProducts(products.Stock);
                                
                            })
                    }}
            >
                <Form>
                    <dl>
                        <dt>Name</dt>
                        <dd><Field type="text" name="Name" onchange={products.ProductID}  value={products[0].Name} /></dd>

                        <dt>Price</dt>
                        <dd><Field type="text" name="Price" value={products[0].Price} /></dd>

                        <dt>Stock</dt>
                        <dd><Field type="checkbox" name="Stock" checked={products[0].Stock} /></dd>
                    </dl>
                    <button type="submit" className="btn btn-success">Save</button>
                    <Link to="/products">Back to Products</Link>
                </Form>
            </Formik>



        </div>
    );
}
