import { useParams, Link, useNavigate } from "react-router-dom";
import axios from "axios";
import "../Shopper-Category/shopperCategoryComponent.css"
import { useState, useEffect } from "react";
import { useCookies } from "react-cookie";



function ShopperCategotyComponent() {

    const params = useParams("");
    const [products, setProducts] = useState([]);
    // eslint-disable-next-line no-unused-vars
    const [cookies, setCookie, removeCookie] = useCookies();
    const navigate = useNavigate()

    useEffect(() => {
        if (cookies["UserID"] === undefined) {
            navigate("/login");
        }
    })
    function SignOut() {
        removeCookie("UserID");
        navigate("/login");
    }



    useEffect(() => {
        axios({
            method: 'get',
            url: `http://fakestoreapi.com/products/category/${params.catname}`
        })
            .then((response) => {
                setProducts(response.data)
            })
    }, [params.catname]);



    return (
        <div className="container-fluid">
            <h2>Shopper Category {params.catname}  - {cookies["UserID"]}</h2>
            <div className="d-flex flex-wrap">
                {
                    products.map((product) =>
                        <div className="card m-2 p-2" key={product.id} >
                            <img src={product.image} className="card-img-top" alt="" />
                            <div className="card-header">
                                <p>{product.title}</p>

                            </div>
                            <div className="card-footer">
                                <Link to={`/details/${product.id}`} className="btn btn-info d-block">Details</Link>
                                <Link className="btn btn-warning  m-1 bi bi-cart4">Add to cart</Link>
                                <Link className="btn btn-danger d-inline m-1">Buy Now</Link>
                            </div>
                        </div>
                    )
                }

            </div>
            <div className="text-end">
                <button onClick={SignOut} className="btn btn-danger btn-link mb-5">Sign Out</button>
            </div>

        </div>
    )


}

export default ShopperCategotyComponent;
