import { Formik, Form, Field, ErrorMessage } from "formik";
import axios from "axios";
import * as yup from "yup";
import { useNavigate } from "react-router-dom";

export function ShopperLogin() {
    const navigate = useNavigate();
    return (
        <div className="container-fluid">
            <h2>User Login</h2>
            <Formik
                initialValues={{
                    UserId: "",
                    Password: "",
                }}

                onSubmit={
                    (values) => {
                        axios({
                            method: "get",
                            url: "http://127.0.0.1:5000/users",
                        })
                            .then((response) => {
                                for(var user of response.data){
                                    if(user.userId == values.UserId && user.password == values.Password){
                                        navigate("/home");
                                    }
                                }
                            })
                    }}
            >
                {
                    <Form>
                        <dl>
                            <dt>UserId</dt>
                            <dd><Field type="text" name="userId" /></dd>
                            <dd className="text-danger">
                                <ErrorMessage name="userId" />
                            </dd>

                            <dt>UserName</dt>
                            <dd><Field type="text" name="userName" /></dd>
                            <dd className="text-danger">
                                <ErrorMessage name="userName" />
                            </dd>
                                                       
                            <dt>Password</dt>
                            <dd><Field type="password" name="password" /></dd>
                            <dd className="text-danger">
                                <ErrorMessage name="password" />
                            </dd>
                            
                            <dt>Email</dt>
                            <dd><Field type="text" name="email" /></dd>
                            <dd className="text-danger">
                                <ErrorMessage name="email" />
                            </dd>

                            <dt>Age</dt>
                            <dd><Field type="number" name="age" /></dd>
                            <dd className="text-danger">
                                <ErrorMessage name="age" />
                            </dd>

                            <dt>Mobile</dt>
                            <dd><Field type="text" name="mobile" /></dd>
                            <dd className="text-danger">
                                <ErrorMessage name="mobile" />
                            </dd>
                        </dl>
                        <button className="btn btn-primary" type="submit">Register</button>
                    </Form>
                }

            </Formik>


        </div>
    )
}
