import axios from "axios";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export function CrudEdit() {
    const params = useParams();
    const [products, setProducts] = useState([{}]);
    // const [products, setProducts] = useState([{productId:0, name:"", price:0, stock:false}]);
    useEffect(() => {
        axios({
            method: 'get',
            url: `http://localhost:8080/details/${params.id}`
        }).then((response) => {
            setProducts(response.data);
        })
    }, [params.id]);

    const handleSubmit = (values) => {
        axios.put(`http://localhost:8080/updateproduct/${values.ProductID}`, values)
            .then((response) => {
                setProducts(values);
            })
            .catch(error => {
                console.error('Error updating product:', error);
            });

    }

    return (
        <div className="container-fluid">
            <h2>Edit Product</h2>
            <Formik
                initialValues={
                    {
                        ProductID: products[0].ProductID,
                        Name: products[0].Name || "",
                        Price: products[0].Price || 0,
                        Stock: products[0].Stock || false
                    }
                }
                onSubmit={handleSubmit}
            >
                <Form>
                    <dl>
                        <dt>Name</dt>
                        <dd><Field type="text" name="Name" /></dd>

                        <dt>Price</dt>
                        <dd><Field type="text" name="Price" /></dd>

                        <dt>Stock</dt>
                        <dd><Field type="checkbox" name="Stock" /></dd>
                    </dl>
                    <button type="submit" onSubmit={handleSubmit} className="btn btn-success">Save</button>
                    <Link to="/products">Back to Products</Link>
                </Form>
            </Formik>
        </div>
    );
}
