export function CrudEdit() {
    const navigate = useNavigate();
    const params = useParams();
    const [products, setProducts] = useState({ ProductID: 0, Name: "", Price: 0, Stock: true });

    useEffect(() => {
        axios({
            method: "get",
            url: `http://127.0.0.1:8080/details/${params.id}`
        }).then((response) => {
            setProducts(response.data[0]);
        });
    }, [params.id]);    

    function HandleProductChange(e) {
        const { name, value, type, checked } = e.target;
        const newValue = type === "checkbox" ? checked : value;
        setProducts(prevState => ({
            ...prevState,
            [name]: newValue
        }));
    }

    return (
        <div className="container-fluid">
            <h2>Update Product</h2>
            <Formik
                initialValues={{
                    ProductID: products.ProductID,
                    Name: products.Name,
                    Price: products.Price,
                    Stock: products.Stock
                }}
                validationSchema={
                    yup.object({
                        Name: yup
                            .string()
                            .required("Name is Required"),
                        Price: yup
                            .number()
                            .min(1)
                            .required("Price is Required"),
                        Stock: yup
                            .boolean()
                            .required("Stock is Required")
                    })
                }
                onSubmit={(values) => {
                    axios({
                        method: "put",
                        url: `http://127.0.0.1:8080/updateproduct/${params.id}`,
                        data: values
                    }).then((response) => {
                        setProducts(response.data);
                        alert("Record Updated Successfully");
                        navigate("/products");
                    });
                }}
            >
                {({ errors, touched }) => (
                    <Form>
                        <dl>
                            <dt>Name</dt>
                            <dd>
                                <Field type="text" name="Name" value={products.Name} onChange={HandleProductChange} />
                                {errors.Name && touched.Name && <div className="text-danger">{errors.Name}</div>}
                            </dd>
                            <dt>Price</dt>
                            <dd>
                                <Field type="number" name="Price" value={products.Price} onChange={HandleProductChange} />
                                {errors.Price && touched.Price && <div className="text-danger">{errors.Price}</div>}
                            </dd>
                            <dt>Stock</dt>
                            <dd className="form-switch">
                                <Field className="form-check-input" type="checkbox" name="Stock" checked={products.Stock} onChange={HandleProductChange} />
                                Available
                                {errors.Stock && touched.Stock && <div className="text-danger">{errors.Stock}</div>}
                            </dd>
                        </dl>
                        <button className="btn btn-success px-4" type="submit">Save</button>
                        <Link className="ms-3" to="/products">View Products</Link>
                    </Form>
                )}
            </Formik>
        </div>
    );
}
