import { Formik, Form, Field, ErrorMessage } from "formik";
import * as yup from "yup";
import { useNavigate, Link, useParams } from "react-router-dom";
import axios from "axios";
import { useEffect, useState } from "react";

export function CrudEdit() {
    const navigate = useNavigate();
    const params = useParams();
    const [product, setProduct] = useState({
        ProductID: 0,
        Name: "",
        Price: 0,
        Stock: true
    });

    useEffect(() => {
        axios.get(`http://127.0.0.1:8080/details/${params.id}`)
            .then((response) => {
                setProduct(response.data);
            })
            .catch((error) => {
                console.error("Error fetching product details:", error);
            });
    }, [params.id]);

    const validationSchema = yup.object().shape({
        Name: yup.string().required("Name is Required"),
        Price: yup.number().min(1).required("Price is Required"),
        Stock: yup.boolean().required("Stock is Required")
    });

    const handleSubmit = (values) => {
        axios.put(`http://127.0.0.1:8080/updateproduct/${params.id}`, values)
            .then((response) => {
                alert("Record Updated Successfully");
                navigate("/products");
            })
            .catch((error) => {
                console.error("Error updating product:", error);
            });
    };

    return (
        <div className="container-fluid">
            <h2>Update Product</h2>
            <Formik
                initialValues={{
                    ProductID: product.ProductID,
                    Name: product.Name,
                    Price: product.Price,
                    Stock: product.Stock
                }}
                enableReinitialize={true}
                validationSchema={validationSchema}
                onSubmit={handleSubmit}
            >
                {({ values, handleChange, handleSubmit }) => (
                    <Form onSubmit={handleSubmit}>
                        <dl>
                            <dt>Name</dt>
                            <dd>
                                <Field type="text" name="Name" />
                                <ErrorMessage name="Name" component="div" className="text-danger" />
                            </dd>
                            <dt>Price</dt>
                            <dd>
                                <Field type="number" name="Price" />
                                <ErrorMessage name="Price" component="div" className="text-danger" />
                            </dd>
                            <dt>Stock</dt>
                            <dd className="form-switch">
                                <Field className="form-check-input" type="checkbox" name="Stock" />
                                <label className="form-check-label">Available</label>
                                <ErrorMessage name="Stock" component="div" className="text-danger" />
                            </dd>
                        </dl>
                        <button type="submit" className="btn btn-success px-4">Save</button>
                        <Link className="ms-3" to="/products">View Products</Link>
                    </Form>
                )}
            </Formik>
        </div>
    );
}
