import axios from "axios";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export function CrudEdit() {
    const params = useParams();
    const [products, setProducts] = useState([{}]);
    // const [products, setProducts] = useState([{productId:0, name:"", price:0, stock:false}]);
    useEffect(() => {
        axios({
            method: 'get',
            url: `http://localhost:8080/details/${params.id}`
        }).then((response) => {
            setProducts(response.data);
        })
    }, []);
    return (
        <div className="container-fluid">
            <h2>Edit Product</h2>
            <Formik
                initialValues={
                    {
                        ProductID: products[0].ProductID,
                        Name: products[0].Name,
                        Price: products[0].Price,
                        Stock: products[0].Stock
                    }
                }
            >
            <Form>
                <dl>
                    <dt>Name</dt>
                    <dd><Field type="text" name="Name" value={products[0].Name} /></dd>
                    <dt>Price</dt>
                    <dd><Field type="text" name="Price" value={products[0].Price} /></dd>
                    <dt>Stock</dt>
                    <dd><Field type="text" name="Stock" value={products[0].Stock} /></dd>
                    <dt><button type="submit">Save</button></dt>
                </dl>
            </Form>

            </Formik>



        </div>
    );
}
