/* eslint-disable no-unused-vars */
import { Formik, Form, Field, ErrorMessage } from "formik"
import axios from "axios"
import * as yup from "yup"
import { useNavigate, Link } from "react-router-dom"
import { useCookies } from "react-cookie"



function ShopperLoginComponent() {

    const [cookies, setCookie, removeCookie] = useCookies();
    const navigate = useNavigate();

    return (
        <div className="container-fluid form-control">
            <h2>USer Login</h2>
            <Formik
                initialValues={{
                    UserID: "",
                    Password: ""
                }}
                validationSchema={
                    yup.object({
                        UserID: yup
                            .string()
                            .required("UserID is Required"),
                        Password: yup
                            .string()
                            .required("Password is Required")

                    })
                }
                onSubmit={
                    (values) => {
                        axios({
                            method: "get",
                            url: "http://127.0.0.1:8081/users"
                        })
                            .then((response) => {
                                for (var user of response.data) {
                                    if (user.UserID === values.UserID && user.Password === values.Password) {
                                        setCookie("UserID", values.UserID)
                                        navigate("/home")
                                        break;
                                    } else {
                                        // alert("Invalid Credentials")
                                        navigate("/invalid")
                                    }
                                }

                            })
                    }
                }>
                <Form>
                    <dl>
                        <dt><label htmlFor="UserID">User ID</label></dt>
                        <dd><Field type="text" name="UserID" /></dd>
                        <dd className="text-danger"><ErrorMessage name="UserID" /></dd>
                        <dt><label htmlFor="Password">Password</label></dt>
                        <dd><Field type="password" name="Password" /></dd>
                        <dd className="text-danger"><ErrorMessage name="Password" /></dd>
                    </dl>
                    <button className="btn btn-primary" type="submit">Login</button>
                    <div>
                        <Link to="/register">Sign Up</Link>
                    </div>
                </Form>

            </Formik>
        </div >
    )


}
export default ShopperLoginComponent
