import React, { useEffect } from "react";
import { useCookies } from "react-cookie";
import { useNavigate } from "react-router-dom";

import { Dropdown } from "react-bootstrap";

const ShopperSignout = () => {
  const [cookies, setCookie, removeCookie] = useCookies(["user"]);
  const navigate = useNavigate();

  useEffect(() => {
    if (!cookies["userId"]) {
      navigate("/login");
    }
  }, []);
  const SignoutClick = () => {
    removeCookie("userId");
    navigate("/login");
  };
  return (
    <Dropdown>
      <Dropdown.Toggle variant="success" id="dropdown-basic">
        {cookies["userId"]}
      </Dropdown.Toggle>

      <Dropdown.Menu>
        <Dropdown.Item href="#/login" onClick={SignoutClick} className="bg-danger text-white cursor-pointer text-center fw-bold">
        {/* className="btn btn-link bg-white text-decoration-none fw-bold text-dark" */}
          Sign out
        </Dropdown.Item>
      </Dropdown.Menu>
    </Dropdown>
  );
};

export default ShopperSignout;
