import axios from "axios";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";


export function CrudIndex() {
    const [products, setProducts] = useState([]);
    useEffect(() => {
        axios({
            method: "get",
            url: "http://127.0.0.1:8080/products",
        }).then((response) => {
            setProducts(response.data);
        });
    }, []);

    function DeleteClick(e) {
        axios({
            method: "delete",
            url: `http://127.0.0.1:8080/products/${event.target.value}`,
        }).then((response) => {
            setProducts(response.data);
        });
    }
    return (
        <div className="container-fluid">
            <h2>Products Grid</h2>
            <div className="mb-3">
                <Link
                    to="/NewProduct"
                    className="btn btn-primary">
                    Add New Product
                </Link>
            </div>
            <table className="table table-hover">
                <thead>
                    <tr>
                        <th>Name</th>
                    </tr>
                </thead>
                <tbody>
                    {
                        products.map((product) => (
                            <tr
                                key={product.productId}>
                                <td>{product.Name}</td>
                                <td>
                                    <Link className="btn btn-info" to={'/crudDetails/' + product.ProductId}>
                                        <span className="bi bi-eye"></span>
                                    </Link>
                                </td>
                                <td>
                                    <Link className="btn btn-warning" to="/details">
                                        <span className="bi bi-pencil"></span>
                                    </Link>
                                </td>
                                <td>
                                    <button value={product.ProductId} className="btn btn-danger" onClick={DeleteClick}>
                                        <span className="bi bi-trash"></span>
                                    </button>
                                </td>
                            </tr>
                        ))
                    }
                </tbody>
            </table>
        </div>
    )
}
