import { useParams, Link } from "react-router-dom";
import { useState, useEffect } from "react";
import axios from "axios";


function ShopperDetailsComponent() {


    const [product, setProduct] = useState({ id: 0, title: '', price: 0, rating: { rate: 0, count: 0 } });
    const params = useParams();
    useEffect(() => {

        axios({
            method: 'get',
            url: `http://fakestoreapi.com/products/${params.id}`
        })
            .then((response) => {
                setProduct(response.data)
            })
    }, [params.id]);


    return (
        <div className="container-fluid">
            <h2>Details</h2>
            <div className="row">
                <div className="col-3">
                    <img src={product.image} width="200" height="200" alt="" />
                </div>
                <div className="col-9">
                    <dl>
                        <dt>Title</dt>
                        <dd>{product.title}</dd>
                        <dt className="text-success">Price</dt>
                        <dd className="text-danger">${product.price}</dd>
                        <dt>Rating</dt>
                        <dd><span className="bi bi-star-fill text-warning"></span>{product.rating.rate}[{product.rating.count}]</dd>
                    </dl>
                    <div>
                        <Link to={`/category/${product.category}`}>Back to {product.category}</Link>
                    </div>
                    <div>
                        <Link className="btn btn-warning m-1 bi bi-cart4">Add to cart</Link>
                        <Link className="btn btn-danger  m-1">Buy Now</Link>
                    </div>
                </div>
            </div>
        </div>
    )

}

export default ShopperDetailsComponent;
