import axios from "axios";
import { Formik, Form, Field, ErrorMessage } from "formik";
import { useEffect, useState } from "react";
import { useParams, Link } from "react-router-dom";

export function CrudEdit() {
    const params = useParams();
    const [product, setProduct] = useState({});

    useEffect(() => {
        axios({
            method: 'get',
            url: `http://localhost:8080/details/${params.id}`
        }).then((response) => {
            setProduct(response.data);
        });
    }, []);

    return (
        <div className="container-fluid">
            <h2>Edit Product</h2>
            <Formik
                initialValues={{
                    ProductID: product.ProductID || 0,
                    Name: product.Name || '',
                    Price: product.Price || 0,
                    Stock: product.Stock || false
                }}
                onSubmit={(values, { setSubmitting }) => {
                    axios({
                        method: 'put',
                        url: `http://localhost:8080/updateproduct/${values.ProductID}`,
                        data: values
                    })
                        .then((response) => {
                            // Update product state after successful update
                            setProduct(values);
                        })
                }}
            >
                <Form>
                    <dl>
                        <dt>Name</dt>
                        <dd><Field type="text" name="Name" /></dd>

                        <dt>Price</dt>
                        <dd><Field type="text" name="Price" /></dd>

                        <dt>Stock</dt>
                        <dd><Field type="checkbox" name="Stock" /></dd>
                    </dl>
                    <button type="submit" className="btn btn-success">Save</button>
                    <Link to="/products">Back to Products</Link>
                </Form>
            </Formik>
        </div>
    );
}
