import React, { useContext, createContext, useState } from "react";

let UserDetailsContext = React.createContext(null);

export function MensClothing() {
    const context = useContext(UserDetailsContext);
    return (
        <div bg-warning p-3>
            <h3>MensClothing : {context.name}</h3>
        </div>
    );
}

export function HomeComponent() {
    const context = useContext(UserDetailsContext);
    return (
        <div className="bg-light text-dark p-3">
            <h3>Home : {context.name}</h3>
            {/* <h3>Age: {context.age}</h3> */}
            <MensClothing />
        </div>
    );
}


export function ContextDemo() 
{
    const
        [name, setName] = useState("John"),
        [age, setAge] = useState(30);

        function updateUserDetails(name, age) {
            setName(name);
            setAge(age);
        }
    return (
        <div className="container-fluid bg-dark text-white p-3" style={{ height: "300px" }}>
            <h2>Main Component</h2>
            <dl>
                <dt>Name</dt>
                <dd><input type="text" onChange={updateUserDetails} /></dd>
                <dt>Age</dt>
                <dd><input type="number" onChange={updateUserDetails} /></dd>
            </dl>
            <UserDetailsContext.Provider value={{ Name: name, Age: age }}>
                <HomeComponent />
            </UserDetailsContext.Provider>
        </div>
    )
}
