import { Formik, Form, Field, ErrorMessage } from "formik"
import * as yup from "yup"
import { useNavigate, Link, useParams } from "react-router-dom"
import axios from "axios"
import { useEffect, useState } from "react"



export function CrudEdit() {
    const navigate = useNavigate();
    const params = useParams();
    const [product, setProduct] = useState([{
        ProductID: 0,
        Name: "",
        Price: 0,
        Stock: false
    }]);

    useEffect(() => {
        axios({
            method: "get",
            url: `http://127.0.0.1:8080/details/${params.id}`

        }).then((response) => {
            setProduct(response.data)
            // alert("Record Updated Successfully")
            // navigate("/product")
        }).catch(error => {
            alert.error('Error fetching product details:', error);
        });
    }, [params.id]);

    const handleChange = (event) => {
        const { name, value, type, checked } = event.target;
        const newValue = type === 'checkbox' ? checked : value;
        setProduct(prevState => ({
            ...prevState,
            [name]: newValue
        }));
    };

    const handleSubmit = (values, { setSubmitting }) => {
        axios.put(`http://127.0.0.1:8080/updateproduct/${params.id}`, product)
            .then((response) => {
                alert("Record Updated Successfully");
                navigate("/products");
            })
            .catch(error => {
                console.error('Error updating product:', error);
            })
            .finally(() => {
                setSubmitting(false);
            });
    };

    // function handleSubmit(e) {
    //     e.preventDefault();
    //     axios({
    //         method: "put",
    //         url: `http://127.0.0.1:8080/updateproduct`,
    //         data: product

    //     }).then((response) => {
    //         // setProduct(product.Name)
    //         // setProduct(product.Price)
    //         // setProduct(product.Stock)
    //         alert("Record Updated Successfully")
    //         navigate("/product")
    //     })
    // }

    return (
        <div className="container-fluid">
            <h2>Update Product</h2>
            <Formik
                initialValues={{
                    ProductID: product.ProductID,
                    Name: product.Name,
                    Price: product.Price,
                    Stock: product.Stock
                }}
                validationSchema={
                    yup.object({
                        Name: yup
                            .string()
                            .required("Name is Required"),
                        Price: yup
                            .number()
                            .min(1)
                            .required("Price is Required"),
                        Stock: yup
                            .boolean()
                    })
                }
            >
                <Form onSubmit={handleSubmit}>
                    <dl>

                        <dt>Name</dt>
                        <dd><Field type="text"
                            onChange={handleChange} name="Name"
                            value={product[0].Name} />
                        </dd>

                        <dt>Price</dt>
                        <dd><Field type="number"
                            onChange={handleChange} name="Price"
                            value={product[0].Price} />
                        </dd>

                        <dt>Stock</dt>
                        <dd className="form-switch">
                            <Field className="form-check-input" type="checkbox" name="Stock"
                                checked={product[0].Stock} />Available</dd>
                    </dl>
                    <button className="btn btn-primary" >Svae</button>
                    <Link className="ms-3" to="/product">View Product</Link>
                </Form>
            </Formik>
        </div>
    )
}
